mapper.select:
SELECT gst_id gstId
FROM guest
WHERE gst_id = #{gstId}

mapper.update:
UPDATE guest 
SET 
	use_yn = (SELECT cd FROM common_code WHERE crp_cd = 'A002' AND cd = 'N'),
	mod_time = NOW(),
	mod_id = #{modId}
WHERE gst_id = #{gstId}

mapper.select:
SELECT gst_name gstName
FROM guest
WHERE gst_name = #{gstName}
AND gst_id = #{gstId}

mapper.update:
UPDATE guest 
SET 
	gst_pw = SHA2(#{newPw}, 256),
	mod_time = NOW(),
	mod_id = #{gstId}
WHERE gst_id = #{gstId}
